package cn.transpad.transpadui.entity;import android.os.Parcel;import android.os.Parcelable;import ca.laplanete.mobile.pageddragdropgrid.Item;/** * Created by Kongxiaojun on 2015/4/8. */public class App implements Parcelable,Item {    private long id;    private String name;    /**     * 位置索引     */    private int index;    /**     * 页面id     */    private int pageId;    /**     * 包名     */    private String packageName;    /**     * Intent Action     */    private String activityName;    /**     * 是否安装     */    private boolean isInstall;    /**     * 图片地址     */    private String imageUrl;    /**     * 下载地址     */    private String downloadUrl;    /**     * 下载id     */    private int downloadId;    /**     * 下载进度     */    private int downloadProgress;    /**     * 文件长度     */    private int length;    /**     * 下载状态     */    private int downloadStateType;    @Override    public long getId() {        return id;    }    @Override    public void setId(long id) {        this.id = id;    }    @Override    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    @Override    public int getType() {        return TYPE_APPLICATION;    }    @Override    public void setIndex(int index) {        this.index = index;    }    @Override    public int getIndex() {        return index;    }    @Override    public int getPageId() {        return pageId;    }    @Override    public void setPageId(int pageId) {        this.pageId = pageId;    }    public String getPackageName() {        return packageName;    }    public void setPackageName(String packageName) {        this.packageName = packageName;    }    public boolean isInstall() {        return isInstall;    }    public void setIsInstall(boolean isInstall) {        this.isInstall = isInstall;    }    public String getImageUrl() {        return imageUrl;    }    public void setImageUrl(String imageUrl) {        this.imageUrl = imageUrl;    }    public String getDownloadUrl() {        return downloadUrl;    }    public void setDownloadUrl(String downloadUrl) {        this.downloadUrl = downloadUrl;    }    public int getDownloadId() {        return downloadId;    }    public void setDownloadId(int downloadId) {        this.downloadId = downloadId;    }    public int getDownloadProgress() {        return downloadProgress;    }    public void setDownloadProgress(int downloadProgress) {        this.downloadProgress = downloadProgress;    }    public int getLength() {        return length;    }    public void setLength(int length) {        this.length = length;    }    public int getDownloadStateType() {        return downloadStateType;    }    public void setDownloadStateType(int downloadStateType) {        this.downloadStateType = downloadStateType;    }    public String getActivityName() {        return activityName;    }    public void setActivityName(String activityName) {        this.activityName = activityName;    }    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel(Parcel dest, int flags) {        dest.writeInt(this.pageId);        dest.writeString(this.packageName);        dest.writeByte(isInstall ? (byte) 1 : (byte) 0);        dest.writeString(this.imageUrl);        dest.writeString(this.downloadUrl);        dest.writeInt(this.downloadId);        dest.writeInt(this.downloadProgress);        dest.writeInt(this.length);        dest.writeInt(this.downloadStateType);        dest.writeLong(this.id);        dest.writeString(this.name);        dest.writeString(this.activityName);    }    public App() {    }    private App(Parcel in) {        this.pageId = in.readInt();        this.packageName = in.readString();        this.isInstall = in.readByte() != 0;        this.imageUrl = in.readString();        this.downloadUrl = in.readString();        this.downloadId = in.readInt();        this.downloadProgress = in.readInt();        this.length = in.readInt();        this.downloadStateType = in.readInt();        this.id = in.readLong();        this.name = in.readString();        this.activityName = in.readString();    }    public static final Parcelable.Creator<App> CREATOR = new Parcelable.Creator<App>() {        public App createFromParcel(Parcel source) {            return new App(source);        }        public App[] newArray(int size) {            return new App[size];        }    };    @Override    public String toString() {        return "App{ name = " + name +                "pageId=" + pageId +                ", packageName='" + packageName + '\'' +                ", isInstall=" + isInstall +                ", imageUrl='" + imageUrl + '\'' +                ", downloadUrl='" + downloadUrl + '\'' +                ", downloadId=" + downloadId +                ", downloadProgress=" + downloadProgress +                ", length=" + length +                ", downloadStateType=" + downloadStateType +                '}';    }}